angular.module("spin.template").run(["$templateCache",function(a){"use strict";a.put("partials/chapter.html",'<div class="chapter chapter-entrance-animation" ng-if="shouldShowChapter()" ng-class="chapterClasses()">    \n\n    <preload images="getBackgrounds()" />\n\n    <div class="chapter__intro" ng-show="user.isStartingChapter() && !user.isGameOver">\n        <div class="chapter__intro__id">Chapitre [[chapter.id]]</div>\n        <h2 class="chapter__intro__title">[[chapter.title]]</h2>  \n    </div>\n\n    <div class="chapter__track" ng-hide="user.isStartingChapter() || user.isGameOver">\n        <div class="chapter__track__id">Chapitre <b>[[chapter.id]]</b> / 6</div>\n        <div class="chapter__track__bullets">\n            <i class="chapter__track__bullets__bullet" ng-repeat="i in [] | range: 6" ng-class="{ active: i <= chapter.id - 1 }"></i>\n        </div>\n        <div class="chapter__track__progression">\n            [[(chapter.id-1)/4*100]]<small>%</small>\n        </div>\n        <h2 class="chapter__track__title">[[chapter.title]]</h2>  \n    </div>\n    \n    \n    <ul class="chapter__cmd list-unstyled" ng-hide="user.isStartingChapter() || user.isGameOver">\n\n        <li class="chapter__cmd__indicator">\n            <span class="chapter__cmd__indicator__label">Guilt</span>\n            <span class="chapter__cmd__indicator__value">[[user.indicators.guilt]]%</span>\n            <span class="chapter__cmd__indicator__progress progress">\n                <span class="progress-bar" ng-style="{ width: user.indicators.guilt + \'%\' }">\n                </span>                \n            </span>\n        </li>\n        <li class="chapter__cmd__indicator">\n            <span class="chapter__cmd__indicator__label">Honesty</span>\n            <span class="chapter__cmd__indicator__value">[[user.indicators.honesty]]%</span>\n            <span class="chapter__cmd__indicator__progress progress">\n                <span class="progress-bar" ng-style="{ width: user.indicators.honesty + \'%\' }">\n                </span>                \n            </span>\n        </li>\n        <li class="chapter__cmd__indicator">\n            <span class="chapter__cmd__indicator__label">Karma</span>\n            <span class="chapter__cmd__indicator__value">[[user.indicators.karma]]%</span>\n            <span class="chapter__cmd__indicator__progress progress">\n                <span class="progress-bar" ng-style="{ width: user.indicators.karma + \'%\' }">\n                </span>                \n            </span>\n        </li>\n\n        <li class="chapter__cmd__indicator chapter__cmd__indicator--ubm">\n            <abbr class="chapter__cmd__indicator__help">\n                ?<span class="chapter__cmd__indicator__help__definition">Unité de Bruit Médiatique : présence de votre crise dans les médias</span>\n            </abbr>\n            <span class="chapter__cmd__indicator__label">UBM</span>\n            <span class="chapter__cmd__indicator__value">[[user.indicators.ubm]]%</span>\n            <span class="chapter__cmd__indicator__progress progress">\n                <span class="progress-bar progress-bar-danger" ng-style="{ width: user.indicators.ubm + \'%\' }">\n                </span>                \n            </span>\n        </li>\n        <li class="chapter__cmd__indicator chapter__cmd__indicator--trust">\n            <abbr class="chapter__cmd__indicator__help">\n                ?<span class="chapter__cmd__indicator__help__definition">Confiance accordée à Louis par le spin doctor</span>\n            </abbr>\n            <span class="chapter__cmd__indicator__label">Confiance</span>\n\n            <span class="chapter__cmd__indicator__value">[[user.indicators.trust]]%</span>\n            <span class="chapter__cmd__indicator__progress progress">\n                <span class="progress-bar progress-bar-info" ng-style="{ width: user.indicators.trust + \'%\' }">\n                </span>                \n            </span>\n        </li>\n        <li class="chapter__cmd__indicator chapter__cmd__indicator--stress">\n            <abbr class="chapter__cmd__indicator__help">\n                ?<span class="chapter__cmd__indicator__help__definition">État émotionnel de Louis</span>\n            </abbr>\n            <span class="chapter__cmd__indicator__label">Stress</span>\n            <span class="chapter__cmd__indicator__value">[[user.indicators.stress]]%</span>\n            <span class="chapter__cmd__indicator__progress progress">\n                <span class="progress-bar progress-bar-success" ng-style="{ width: user.indicators.stress + \'%\' }">\n                </span>                \n            </span>\n        </li>\n    </ul>\n\n    <scene ng-repeat="scene in chapter.scenes" src="scene" chapter="chapter"></scene>    \n\n</div>\n'),a.put("partials/debug-toolbar.html",'<div class="debug-toolbar" ng-init="hidden = true" ng-class="{\'debug-toolbar--hidden\': hidden}">\n    <h3>Debug toolbar</h3>\n    <div class="debug-toolbar__controls">\n        <button class="btn btn-default" ng-click="restartCurrentChapter()">\n            Restart current chapter\n        </button>\n        <button class="btn btn-default" ng-click="restartGame()">\n            Restart the game\n        </button>\n        <button class="btn btn-default" ng-click="gameOver()">\n            Game over\n        </button>\n    </div>\n    <div class="debug-toolbar__summary text-right">\n        <div class="form-inline form-group">\n            <label>Current Chapter&nbsp;:</label>\n            <input ng-model="currentChapter" type="text" class="debug-toolbar__summary__info form-control"/>\n            <button class="btn btn-default btn-xs" ng-click="setChapter(currentChapter)">change</button>            \n        </div>\n        <div class="form-inline form-group">\n            <label>Current Scene&nbsp;:</label>\n            <input ng-model="currentScene" type="text" class="debug-toolbar__summary__info form-control"/>\n            <button class="btn btn-default btn-xs" ng-click="setScene(currentScene)">change</button>            \n        </div>\n        <div class="form-inline form-group">\n            <label>Current Sequence&nbsp;:</label>\n            <input ng-model="currentSequence" type="text" class="debug-toolbar__summary__info form-control"/>\n            <button class="btn btn-default btn-xs" ng-click="setSequence(currentSequence)">change</button>            \n        </div>\n    </div>\n    <button ng-click="hidden = !hidden"  class="btn btn-default debug-toolbar__toggler">\n        <span class="caret" ng-class="{ \'caret-reversed\': !hidden }"></span>\n    </button>\n</div>'),a.put("partials/game-over.html",'<div class="game-over" ng-controller="GameOverCtrl" ng-show="user.isGameOver">\n    <div class="game-over__message">\n        <h1>GAME OVER</h1>\n        <p class="game-over__message__description">\n            You lose, it\'s kinda bad yah know. Authentic tattooed mlkshk, hella Truffaut trust fund chillwave Wes Anderson brunch pickled Echo Park put a bird on it Neutra. Photo booth Brooklyn mustache sriracha McSweeney\'s umami tofu leggings sustainable, organic Neutra squid blog.\n        <p>\n    </div>\n\n    <button ng-click="user.newUser(); user.inGame = false; user.isGameOver = false" \n        class="game-over__restart btn btn-default btn-lg">\n        Recommencer\n    </div>\n</div>\n'),a.put("partials/main.html",'<!-- Welcome screen -->\n<div ng-include="\'partials/welcome.html\'"></div>\n<!-- Chapter list to allow supperposition and pre-loading -->\n<chapter ng-repeat="chapter in plot.chapters" src="chapter"></chapter>\n<!-- Results page -->\n<div ng-include="\'partials/results.html\'"></div>\n<!-- Game Over page -->\n<div ng-include="\'partials/game-over.html\'"></div>'),a.put("partials/results.html",'<div class="results" ng-controller="ResultsCtrl" ng-show="shouldShowResults()">\n    <div class="results__feedbacks">\n        <p>Ne rester pas seul(e) dans cette crise,<br /> partagez votre expérience.</p>\n        <div class="results__feedbacks__share">\n            <button class="btn btn-default results__feedbacks__share__facebook">\n                <span class="sr-only">Facebook</span>\n            </button>\n            <button class="btn btn-default results__feedbacks__share__twitter">\n                <span class="sr-only">Twitter</span>\n            </button>\n        </div>\n        \n        <div ng-repeat="(key, feedback) in currentResults.list" class="results__feedbacks__sample">\n            <h3 ng-bind="feedback.title"></h3>\n            <p ng-bind-html="feedback.percentage.sentence"></p>\n            <div class="progress results__feedbacks__sample__progress">\n                <div class="progress-bar" ng-style="feedback.percentage.style">\n                    [[ feedback.percentage.raw ]]%\n                </div>\n            </div>\n        </div>\n        <div class="results__feedbacks__nav">\n            <button ng-click="goNextChapter()" class="btn btn-default results__feedbacks__nav__continue">\n                Continuer\n            </button>\n        </div>\n    </div>\n\n    <div class="results__choices" ng-class="{ \'results__choices--on\': shouldShowChoices }" ng-if="hasPreviousResults()">\n        <button class="results__choices__toggler btn btn-default" ng-click="shouldShowChoices = !shouldShowChoices">\n            Vos choix précédents\n        </button>\n        <div class="results__choices__panel">\n            <div class="results__choices__panel__chapter" ng-repeat="(chapter_key, otherResults) in previousResults()">\n                <h4 class="results__choices__panel__chapter__title">Chapter [[otherResults.chapter.id]]</h4>\n                <div class="results__choices__panel__chapter__percentage" ng-repeat="otherResult in otherResults.list">\n                    [[ otherResult.userChoice.title ]]\n                    <div class="progress results__choices__panel__chapter__percentage__progress">\n                        <div class="progress-bar" ng-style="otherResult.percentage.style"></div>\n                    </div>\n                </div>\n        </div>\n    </div>\n</div>'),a.put("partials/scene.html",'<div class="scene scene-entrance-animation" ng-if="shouldShowScene()">              \n\n    <!-- This is the main illustration -->      \n    <img class="scene__bg" ng-src="[[bg.src | media]]" ng-repeat="bg in getSceneBgs() | filter:shouldDisplayBg" /> \n      \n    <!-- Scene\'s sequence -->\n    <div ng-repeat="sequence in scene.sequence" \n         class="scene__sequence scene__sequence--[[sequence.type]]"\n         ng-show="shouldShowSequence($index)">\n\n        <!-- Dialog box, a the bottom of the screen -->\n        <div ng-if="sequence.isDialog()">\n            <div class="scene__sequence--[[sequence.type]]__frame">            \n                <p class="scene__sequence--[[sequence.type]]__frame__header" ng-if="sequence.header">\n                    [[sequence.header]]\n                </p>\n                <p class="scene__sequence--[[sequence.type]]__frame__character" ng-if="sequence.character">\n                    [[sequence.character]]&nbsp;:\n                </p>\n                <div class="scene__sequence--[[sequence.type]]__frame__body" btf-markdown="sequence.body"></div>\n                <div class="scene__sequence--[[sequence.type]]__frame__head" ng-if="sequence.getHeadSrc()">\n                    <img ng-src="[[sequence.getHeadSrc()]]" />\n                </div>\n            </div>\n            <a class="scene__sequence--dialogue__next btn btn-default" ng-show="$parent.sequence.hasNext()" ng-click="goToNextSequence()">\n                <span class="sr-only">Continuer</span>\n            </a>\n        </div>\n\n        <!-- Player box, a the bottom of the screen -->\n        <div ng-if="sequence.isPlayer()">\n            <p class="scene__sequence--voixoff__character" ng-if="sequence.character">\n                [[sequence.character]]&nbsp;:                \n            </p>\n            <button class="btn btn-default scene__sequence--voixoff__play" ng-click="toggleVoicetrack()" ng-class="{ \'active\': sound.voicetrack.isPlaying }">\n                <span class="sr-only" ng-show="sound.voicetrack.isPlaying">Pause<span>\n                <span class="sr-only" ng-hide="sound.voicetrack.isPlaying">Play<span>\n            </button>                \n            <div class="scene__sequence--voixoff__progression">\n                <div class="progress">                \n                    <div class="progress-bar" role="progressbar" ng-style="{ width: (sound.voicetrack._position/sound.voicetrack._duration)*100 + \'%\'}"></div>\n                </div>\n                <span class="scene__sequence--voixoff__progression__timestamp">\n                    [[sound.voicetrack._position|minutes]] / [[sound.voicetrack._duration|minutes]]\n                </span>\n            </div>\n            <div class="scene__sequence--voixoff__head" ng-if="sequence.getHeadSrc()">\n                <img ng-src="[[sequence.getHeadSrc()]]" />\n            </div> \n            <a class="scene__sequence--dialogue__next btn btn-default" ng-show="$parent.sequence.hasNext()" ng-click="goToNextSequence()">\n                <span class="sr-only">Continuer</span>\n            </a>\n        </div>\n\n        <!-- Choice box, at the middle of the screen -->\n        <div ng-if="sequence.isChoice()">\n            <h3 class="scene__sequence--choice__question">\n                [[sequence.body]]\n            </h3>\n            <button ng-repeat="option in sequence.options"\n                    class="btn btn-lg btn-default scene__sequence--choice__option" \n                    ng-click="selectOption(option, $index)">\n                [[option.header]]\n            </button>\n            <div ng-if="sequence.delay" class="scene__sequence--choice__timeout">\n                <div circle \n                     percentage="timeout.remainingTime"\n                     colors="transparent,#FF7F00"\n                     width="5"\n                     number=""\n                     radius="25"></div>\n            </div>\n        </div>\n\n        <!-- Notification, a the top right corner of the screen -->\n        <div ng-if="sequence.isNotification()">\n            <div class="scene__sequence--notification__header">[[sequence.header]]&nbsp;:</div>\n            <div class="scene__sequence--notification__body" btf-markdown="sequence.body"></div>            \n        </div>\n\n\n        <!-- Feedback, in the middle of the screen -->\n        <div ng-if="sequence.isFeedback()">\n            <div class="scene__sequence--feedback__body" btf-markdown="sequence.body"></div>            \n        </div>\n\n    </div>   \n</div>'),a.put("partials/welcome.html",'<div class="welcome" ng-controller="WelcomeCtrl" ng-hide="user.inGame">\n\n    <div class="welcome__bg"></div>\n\n    <div class="welcome__wrapper">\n        <div class="welcome__intro">\n            <h1>Jeu d\'influences</h1>            \n            <p class="welcome__intro__lead">\n                Jusqu\'où suivrez-vous votre Spin Doctor ?\n            </p>\n            <p>\n                "Je m\'appelle Louis Esmond. J\'ai 41 ans.<br />\n                Je suis l\'ex-PDG de l\'enreprise de BTP Batisain,<br />\n                leader national des constructions labellissées HQE,<br />\n                <em>Haute Qualité Environnementale</em>.\n            </p>\n            <p>\n                Je sios également ex-secrétaire d\'État à la biodiversité<br />\n                auprès du Ministre de l\'Industrie, Martin Jarendeau.\n            </p>\n            <p>\n                L\'histoire qui va suivre raconte comment je suis arrivé jusqu\'ici.<br />\n                <strong>À moins que vous ne m\'emmeniez ailleurs...</strong>"\n            </p>\n        </div>\n\n        <div class="welcome__user">\n            <p>Renseignez votre e-mail pour sauvegarder votre progression</p>       \n            <form class="form-horizontal welcome__user__form" role="signin" ng-submit="submit()" name="form">                     \n                <input class="form-control" placeholder="email@exemple.org" ng-model="email" required type="email" class="form-control" /> \n                <button type="submit" class="welcome__user__form__submit btn btn-default" ng-disabled="form.$invalid">OK</button>\n            </form>\n        </div>\n\n        <button class="welcome__start btn btn-default btn-lg" ng-click="user.inGame = true">Commencer</button>\n    </div>\n</div>\n')}]);